function burgerMenu(){const e=document.querySelector(".header__burger"),i=document.querySelector(".header__menu"),r=document.querySelector("body");e.addEventListener("click",(()=>{i.classList.contains("active")?(i.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")):(i.classList.add("active"),e.classList.add("active-burger"),r.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(i.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("header__nav");window.scrollY>=500?e.classList.add("fixed"):e.classList.remove("fixed")}burgerMenu(),window.addEventListener("scroll",fixedNav);let swiper=new Swiper(".swiper",{slidesPerView:perView(),loop:!0,freeMode:!0,autoplay:{delay:2e3,disableOnInteraction:!1},direction:getDirection(),on:{resize:function(){swiper.changeDirection(getDirection())}}});function perView(){return window.innerWidth<=768?3:5}function getDirection(){window.innerWidth;return window.innerWidth<=100?"vertical":"horizontal"}function scrollWin(){window.scrollBy(0,600)}perView();